(51, 'woocommerce-WCReturnsWarranty-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce Returns and Warranty Requests extension', '<strong>Your store requires a security update for the Returns and Warranty Requests extension</strong>.  Please update to the latest version of the WooCommerce Returns and Warranty Requests extension immediately to address a potential vulnerability discovered on May 31.', '{}', 'pending', 'woocommerce.com', '2023-08-25 06:56:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'woocommerce-WCOPC-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce One Page Checkout', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce One Page Checkout extension</strong>. The fix for this vulnerability was released for this extension on June 13th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2023-08-25 06:56:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(53, 'woocommerce-WCGC-July-2023-update-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce GoCardless Extension', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce GoCardless extension</strong>. The fix for this vulnerability was released on July 4th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2023-08-25 06:56:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'woocommerce-shipping-fedex-api-outage-2023-07-16', 'warning', 'en_US', 'Scheduled FedEx API outage — July 2023', 'On July 16 there will be a full outage of the FedEx API from 04:00 to 08:00 AM UTC. Due to planned maintenance by FedEx, you\'ll be unable to provide FedEx shipping rates during this time. Follow the link below for more information and recommendations on how to minimize impact.', '{}', 'pending', 'woocommerce.com', '2023-08-25 06:56:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'wcship-2023-07-hazmat-update-needed', 'update', 'en_US', 'Action required: USPS HAZMAT compliance update for WooCommerce Shipping & Tax extension', '<strong>Your store requires an update for the WooCommerce Shipping extension</strong>. Please update to the latest version of the WooCommerce Shipping &amp; Tax extension immediately to ensure compliance with new USPS HAZMAT rules currently in effect.', '{}', 'pending', 'woocommerce.com', '2023-08-25 06:56:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'woocommerce-WCStripe-Aug-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe plugin', '<strong>Your shop requires an important security update for the  WooCommerce Stripe plugin</strong>. The fix for this vulnerability was released on July 31. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2023-08-25 06:56:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'woocommerce-WCStripe-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooCommerce Stripe plugin', '<strong>Your store has been updated to the latest secure version of the WooCommerce Stripe plugin</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2023-08-25 06:56:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'woocommerce-WooPayments-Aug-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooPayments (WooCommerce Payments) plugin', '<strong>Your shop requires an important security update for the WooPayments (WooCommerce Payments) extension</strong>. The fix for this vulnerability was released on July 31. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2023-08-25 06:56:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'woocommerce-WooPayments-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooPayments (WooCommerce Payments) plugin', '<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2023-08-25 06:56:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2023-08-25 06:56:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(61, 'woo-activation-survey-blockers-22AUG23', 'info', 'en_US', 'How can we help you get that first sale?', 'Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!', '{}', 'unactioned', 'woocommerce.com', '2023-08-26 06:56:35', NULL, 0, 'plain', '', 0, 1, 'info'),
(62, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to WooCommerce.com', 'Connect to get important product notifications and updates.', '{}', 'unactioned', 'woocommerce-admin', '2023-08-25 06:56:13', NULL, 0, 'plain', '', 0, 1, 'info'),
(63, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Choosing a theme?', 'Check out the themes that are compatible with WooCommerce and choose one aligned with your brand and business needs.', '{}', 'unactioned', 'woocommerce-admin', '2023-08-26 06:56:34', NULL, 0, 'plain', '', 0, 0, 'info'),
(64, 'wc-admin-launch-checklist', 'info', 'en_US', 'Готовы запустить собственный магазин?', 'Чтобы у вас не возникало чувство, что вы что-то забыли, мы составили контрольный список для проверки настроек перед запуском.', '{}', 'unactioned', 'woocommerce-admin', '2023-08-26 06:56:34', NULL, 0, 'plain', '', 0, 0, 'info'),
(65, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'How to customize your product catalog', 'You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.', '{}', 'unactioned', 'woocommerce-admin', '2023-08-27 06:56:56', NULL, 0, 'plain', '', 0, 1, 'info'),
(66, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.', '{}', 'unactioned', 'woocommerce-admin', '2023-08-27 06:56:56', NULL, 0, 'plain', '', 0, 1, 'info'),
(67, 'wc-admin-onboarding-payments-reminder', 'info', 'en_US', 'Start accepting payments on your store!', 'Take payments with the provider that’s right for you - choose from 100+ payment gateways for WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2023-08-30 10:23:29', NULL, 0, 'plain', '', 0, 1, 'info'),
(68, 'tiktok_shopGTM_q3-2023_noTikTok', 'marketing', 'en_US', 'You can now sell your products on TikTok Shop', 'TikTok is changing the way shoppers find and connect with brands. With TikTok Shop, you can sync your entire catalog from within Woo and sell directly to TikTok users through in-feed videos, livestreams, and your product showcase tab.', '{}', 'pending', 'woocommerce.com', '2023-09-08 15:03:54', NULL, 0, 'plain', '', 0, 0, 'info'),
(69, 'wc_admin_BNPL_WCPay_Q3_2023', 'marketing', 'en_US', 'Afterpay and Affirm have arrived in WooCommerce Payments!', 'Give your customers more power with buy now, pay later from Afterpay and Affirm — now included as part of WooCommerce Payments! With this new integration, you can quickly offer a checkout option that converts sales and boosts average order value, all from within the same payments dashboard you already know.', '{}', 'pending', 'woocommerce.com', '2023-09-27 13:33:40', NULL, 0, 'plain', '', 0, 0, 'info'),
(70, 'wc_admin_afterpay_WCPay_Q3_2023', 'marketing', 'en_US', 'Afterpay has arrived in WooCommerce Payments!', 'Give your customers more power with buy now, pay later from Afterpay — now included as part of WooCommerce Payments! With this new integration, you can quickly offer a checkout option that converts sales and boosts average order value, all from within the same payments dashboard you already know.', '{}', 'pending', 'woocommerce.com', '2023-09-27 13:33:40', NULL, 0, 'plain', '', 0, 0, 'info'),
(71, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2023-09-27 13:33:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(72, 'wc_com_in_person_payments_card_sale_US_inbox_note_q4_23', 'marketing', 'en_US', 'Save 35% on a WooPayments card reader', 'Have you been thinking about selling your products or services in person? Now’s the perfect time to do so, as we’re discounting our M2 card readers by 35%! Securely process payments wherever your business takes you, with full store and inventory sync. Don’t miss out — this offer ends October 29, 2023.', '{}', 'pending', 'woocommerce.com', '2023-10-16 05:11:35', NULL, 0, 'plain', '', 0, 0, 'info'),
(73, 'wc_com_in_person_payments_card_sale_UK_inbox_note_q4_23', 'marketing', 'en_US', 'Save 35% on a WooPayments card reader', 'Have you been thinking about selling your products or services in person? Now’s the perfect time to do so, as we’re discounting our WisePad 3 card readers by 35%! Securely process payments wherever your business takes you, with full store and inventory sync. Don’t miss out — this offer ends 29th October 2023.', '{}', 'pending', 'woocommerce.com', '2023-10-16 05:11:35', NULL, 0, 'plain', '', 0, 0, 'info'),
